	public static function add_menu_item(){		
		if ($this->user_functions->isMerchStockUser()){
			add_menu_page( "Dashboard", "Dashboard", "read", "dashboard", array('Merch_Stock_Menu','get_dashboard'), 'data:image/png;base64,', null );	
			if ($this->user_functions->getMerchStockRole()=="ms_admin"){
				add_menu_page( "Products", "Products", "read", "products_admin", array('Merch_Stock_Menu','get_products_admin'), 'data:image/png;base64,', null );			
				add_menu_page( "Offices", "Offices", "read", "offices", array('Merch_Stock_Menu','get_offices'), 'data:image/png;base64,', null );	
				add_menu_page( "Orders", "Orders", "read", "orders", array('Merch_Stock_Menu','get_orders'), 'data:image/png;base64,', null );			
				add_menu_page( "Backorders", "Backorders", "read", "backorders", array('Merch_Stock_Menu','get_backorders'), 'data:image/png;base64,', null );		
			}
			else if ($this->user_functions->getMerchStockRole()=="ms_manager"){
				add_menu_page( "Products", "Products", "read", "products", array('Merch_Stock_Menu','get_products'), 'data:image/png;base64,', null );	
				add_menu_page( "Orders", "Orders", "read", "orders", array('Merch_Stock_Menu','get_orders'), 'data:image/png;base64,', null );				
			}
			else if ($this->user_functions->getMerchStockRole()=="ms_headoffice"){
				add_menu_page( "Products", "Products", "read", "products", array('Merch_Stock_Menu','get_products'), 'data:image/png;base64,', null );		
				add_menu_page( "Orders", "Orders", "read", "orders", array('Merch_Stock_Menu','get_orders'), 'data:image/png;base64,', null );			
				add_menu_page( "Backorders", "Backorders", "read", "backorders", array('Merch_Stock_Menu','get_backorders'), 'data:image/png;base64,', null );		
				add_menu_page( "Product Requests", "Product Requests", "read", "product-requests", array('Merch_Stock_Menu','get_product_requests'), 'data:image/png;base64,', null );
				add_menu_page( "Offices", "Offices", "read", "offices", array('Merch_Stock_Menu','get_offices'), 'data:image/png;base64,', null );				 								
			}
		}
	}
