	public static function remove_menu_item(){
		if ($this->user_functions->isMerchStockUser()){
			remove_menu_page( 'update-core.php' );                   //Posts
			remove_menu_page( 'index.php' );                   //Posts
			remove_menu_page( 'edit.php' );                   //Posts
			remove_menu_page( 'upload.php' );                 //Media
			remove_menu_page( 'edit-comments.php' );          //Comments
			remove_menu_page( 'themes.php' );                 //Appearance
			remove_menu_page( 'users.php' );                  //Users
			remove_menu_page( 'profile.php' );                  //Users
			remove_menu_page( 'tools.php' );                  //Tools
			remove_menu_page( 'options-general.php' );        //Settings	
		    remove_submenu_page('users.php', 'profile.php');
		    remove_menu_page('profile.php');
			// // Remove admin footer and version info
			add_filter( 'admin_footer_text' , array('Merch_Stock_Menu','change_footer_admin'), 9999);
			add_filter( 'update_footer', array('Merch_Stock_Menu','change_footer_version'), 9999);					
			// Remove screen options button
			add_filter('screen_options_show_screen', array('Merch_Stock_Menu'	,'remove_screen_options'), 10, 2);
		}
	}	
	public static function change_footer_admin () {return '';}

	public static function change_footer_version() {return '';}	

	public static function remove_screen_options($display_boolean, $wp_screen_object){
		return true;
		$blacklist = array('post.php', 'post-new.php', 'index.php', 'edit.php');
		if (in_array($GLOBALS['pagenow'], $blacklist)) {
			$wp_screen_object->render_screen_layout();
			$wp_screen_object->render_per_page_options();
		    return false;
		}
		else{
		  return true;
		}
	}	

