	public static function admin_default_page($url, $request, $user){
		if(!$isMerchStockUser){
			return admin_url();
			exit;
			wp_die();
		}
		$ms_role = $this->user_functions->getMerchStockRoleByID($user->ID);
		if ($ms_role=="ms_headoffice"){
			return admin_url('admin.php?page=dashboard');
			exit;
			wp_die();
		}
		else{
			return admin_url('admin.php?page=products');
			exit;
			wp_die();
		}		
    }		
		
	public static function add_menu_item_class(){
