		$order_id = intval($_GET["order_id"]);
		$errors = sanitize_text_field($_GET["errors"]);
		$order = get_post($order_id);
		$order_status = get_post_meta( $order_id, 'status', true);
		$template = plugin_dir_path( __FILE__ ) . '../order.php';
		echo load_template( $template, true );				
